import { DrawuiThemeProvider, DrawuiButton } from  '@carandesign/drawui';

function App() {
  return (
    <DrawuiThemeProvider>
      <DrawuiButton>Click me!</DrawuiButton>
    </DrawuiThemeProvider>
  );
}

interface DrawuiTheme {
  stroke: Record<StrokeWeight, DrawuiStroke>;
  roughness: DrawuiRoughness;
  fill: { background: string };
  radius: Record<RadiusToken, number>;
  buttonSize: Record<SizeToken, DrawuiButtonSize>;
  inputSize: Record<SizeToken, DrawuiInputSize>;
}

import { DrawuiThemeProvider } from '@carandesign/drawui';

function App() {
  return (
    <DrawuiThemeProvider>
      {/* Your app components */}
    </DrawuiThemeProvider>
  );
}

import { DrawuiThemeProvider } from '@carandesign/drawui';
import type { DrawuiTheme } from '@carandesign/drawui';

const customTheme: Partial<DrawuiTheme> = {
  stroke: {
    thin: { color: '#2c3e50', width: 1.5 },
    medium: { color: '#2c3e50', width: 2.5 },
    thick: { color: '#2c3e50', width: 4 },
  },
  fill: {
    background: '#ecf0f1',
  },
  roughness: {
    roughness: 2,
    bowing: 1,
  },
};

function App() {
  return (
    <DrawuiThemeProvider theme={customTheme}>
      {/* Your app components */}
    </DrawuiThemeProvider>
  );
}

const rc = rough.svg(svgRef.current);
const node = rc.path(
  roundedRect(0, 0, width, height, radius),
  {
    stroke: strokeColor,
    strokeWidth: strokeWidth,
    fill: backgroundColor,
    roughness: roughnessValue,
    bowing: bowingValue,
    fillStyle: fillStyle,
  }
);
